// SPDX-License-Identifier: Apache-2.0
pragma solidity ^0.8.19;

import { IMerkleProofSingleUse } from "./IMerkleProofSingleUse.sol";

import { MerkleProof } from "openzeppelin-contracts/contracts/utils/cryptography/MerkleProof.sol";

/**
 * Require single use merkle proofs per address.
 */
abstract contract MerkleProofSingleUse is IMerkleProofSingleUse {

    // Stores proofs used by an address
    mapping(address => mapping(bytes32 => bool)) private _proofUsed;

    /**
     * Requires the given merkle proof to be valid.
     * @param root Merkle root.
     * @param proof Merkle proof.
     * @param addr Address to check.
     * @param salt Salt used to generate the merkle leaf.
     * @notice Fails when the proof is invalid or the proof has already been claimed by this address.
     * @dev This function reverts on failure.
     */
    function requireMerkleProof(bytes32 root, bytes32[] calldata proof, address addr, bytes32 salt) internal {
        if (root != bytes32(0)) {
            if (!checkMerkleProof(root, proof, addr, salt)) {
                revert MerkleProofInvalid(root, proof, addr, salt);
            }
            _proofUsed[addr][root] = true;
        }
    }

    /**
     * Checks if the given merkle proof is valid.
     * @param root Merkle root.
     * @param proof Merkle proof.
     * @param addr Address to check.
     * @param salt Salt used to generate the merkle leaf.
     * @return True if the proof is valid and has not yet been used by {addr}.
     */
    function checkMerkleProof(
        bytes32 root,
        bytes32[] calldata proof,
        address addr,
        bytes32 salt
    ) public view returns (bool) {
        return !_proofUsed[addr][root] && MerkleProof.verify(proof, root, keccak256(abi.encodePacked(addr, salt)));
    }

}

src/tokens/common/SignalsImplicitModeCon.content:
// SPDX-License-Identifier: Apache-2.0
pragma solidity ^0.8.19;

import { AccessControlEnumerable } from "openzeppelin-contracts/contracts/access/AccessControlEnumerable.sol";
import {
    IERC165,
    IImplicitProjectValidation,
    SignalsImplicitMode
} from "signals-implicit-mode/src/helper/SignalsImplicitMode.sol";

/**
 * An abstract contract that allows implicit session access for a given project.
 */
abstract contract SignalsImplicitModeControlled is AccessControlEnumerable, SignalsImplicitMode {

    bytes32 internal constant _IMPLICIT_MODE_ADMIN_ROLE = keccak256("IMPLICIT_MODE_ADMIN_ROLE");

    function _initializeImplicitMode(address owner, address validator, bytes32 projectId) internal {
        _grantRole(_IMPLICIT_MODE_ADMIN_ROLE, owner);
        _initializeSignalsImplicitMode(validator, projectId);
    }

    /**
     * Updates the validator for implicit mode validation.
     * @param validator The validator address.
     * @notice Only callable by an address with the project admin role.
     */
    function setImplicitModeValidator(
        address validator
    ) external onlyRole(_IMPLICIT_MODE_ADMIN_ROLE) {
        _validator = IImplicitProjectValidation(validator);
    }

    /**
     * Updates the settings for implicit mode validation.
     * @param projectId The project id.
     * @notice Only callable by an address with the project admin role.
     */
    function setImplicitModeProjectId(
        bytes32 projectId
    ) external onlyRole(_IMPLICIT_MODE_ADMIN_ROLE) {
        _projectId = projectId;
    }

    /// @inheritdoc IERC165
    function supportsInterface(
        bytes4 interfaceId
    ) public view virtual override(AccessControlEnumerable, SignalsImplicitMode) returns (bool) {
        return
            AccessControlEnumerable.supportsInterface(interfaceId) || SignalsImplicitMode.supportsInterface(interfaceId);
    }

}

src/tokens/common/WithdrawControlled.sol.content:
// SPDX-License-Identifier: Apache-2.0
pragma solidity ^0.8.19;

import { IWithdrawControlled } from "./IWithdrawControlled.sol";

import { AccessControlEnumerable, ERC165 } from "openzeppelin-contracts/contracts/access/AccessControlEnumerable.sol";
import { IERC20, SafeERC20 } from "openzeppelin-contracts/contracts/token/ERC20/utils/SafeERC20.sol";

/**
 * An abstract contract that allows ETH and ERC20 tokens stored in the contract to be withdrawn.
 */
abstract contract WithdrawControlled is AccessControlEnumerable, IWithdrawControlled {

    bytes32 internal constant WITHDRAW_ROLE = keccak256("WITHDRAW_ROLE");

    //
    // Withdraw
    //

    /**
     * Withdraws ERC20 tokens owned by this contract.
     * @param token The ERC20 token address.
     * @param to Address to withdraw to.
     * @param value Amount to withdraw.
     * @notice Only callable by an address with the withdraw role.
     */
    function withdrawERC20(address token, address to, uint256 value) public onlyRole(WITHDRAW_ROLE) {
        SafeERC20.safeTransfer(IERC20(token), to, value);
    }

    /**
     * Withdraws ETH owned by this sale contract.
     * @param to Address to withdraw to.
     * @param value Amount to withdraw.
     * @notice Only callable by an address with the withdraw role.
     */
    function withdrawETH(address to, uint256 value) public onlyRole(WITHDRAW_ROLE) {
        (bool success,) = to.call{ value: value }("");
        if (!success) {
            revert WithdrawFailed();
        }
    }

    /// @inheritdoc ERC165
    function supportsInterface(
        bytes4 interfaceId
    ) public view virtual override(AccessControlEnumerable) returns (bool) {
        return interfaceId == type(IWithdrawControlled).interfaceId
            || AccessControlEnumerable.supportsInterface(interfaceId);
    }

}

