pragma solidity ^0.8.19;

import { ERC20BaseToken } from "../../ERC20BaseToken.sol";
import { IERC20Items, IERC20ItemsFunctions } from "./IERC20Items.sol";

/**
 * A ready made implementation of ERC-20 capable of minting when role provided.
 */
contract ERC20Items is ERC20BaseToken, IERC20Items {

    bytes32 internal constant MINTER_ROLE = keccak256("MINTER_ROLE");

    address private immutable _initializer;
    bool private _initialized;

    constructor() {
        _initializer = msg.sender;
    }

    /**
     * Initialize contract.
     * @param owner The owner of the contract
     * @param tokenName Name of the token
     * @param tokenSymbol Symbol of the token
     * @param tokenDecimals Number of decimals
     * @param implicitModeValidator The implicit mode validator address
     * @param implicitModeProjectId The implicit mode project id
     * @dev This should be called immediately after deployment.
     */
    function initialize(
        address owner,
        string memory tokenName,
        string memory tokenSymbol,
        uint8 tokenDecimals,
        address implicitModeValidator,
        bytes32 implicitModeProjectId
    ) public virtual override {
        if (msg.sender != _initializer || _initialized) {
            revert InvalidInitialization();
        }

        ERC20BaseToken.initialize(
            owner, tokenName, tokenSymbol, tokenDecimals, implicitModeValidator, implicitModeProjectId
        );

        _grantRole(MINTER_ROLE, owner);

        _initialized = true;
    }

    //
    // Minting
    //

    /**
     * Mint tokens.
     * @param to Address to mint tokens to.
     * @param amount Amount of tokens to mint.
     * @notice This function can only be called by a items.
     */
    function mint(address to, uint256 amount) external onlyRole(MINTER_ROLE) {
        _mint(to, amount);
    }

    //
    // Admin
    //

    /**
     * Set name and symbol of token.
     * @param tokenName Name of token.
     * @param tokenSymbol Symbol of token.
     */
    function setNameAndSymbol(
        string memory tokenName,
        string memory tokenSymbol
    ) external onlyRole(DEFAULT_ADMIN_ROLE) {
        _tokenName = tokenName;
        _tokenSymbol = tokenSymbol;
    }

    //
    // Views
    //

    /**
     * Check interface support.
     * @param interfaceId Interface id
     * @return True if supported
     */
    function supportsInterface(
        bytes4 interfaceId
    ) public view virtual override returns (bool) {
        return type(IERC20ItemsFunctions).interfaceId == interfaceId || ERC20BaseToken.supportsInterface(interfaceId)
            || super.supportsInterface(interfaceId);
    }

}
","keccak256":"0x1f41ee30ef18bf3309068c4d57bd7c76684b1b68ba55536278f9ee4d25ac5a4d","license":"Apache-2.0"},"src/tokens/ERC20/presets/items/ERC20ItemsFactory.sol":{"content":"// SPDX-License-Identifier: Apache-2.0
pragma solidity ^0.8.19;

import { SequenceProxyFactory } from "../../../../proxies/SequenceProxyFactory.sol";
import { ERC20Items } from "./ERC20Items.sol";
import { IERC20ItemsFactory, IERC20ItemsFactoryFunctions } from "./IERC20ItemsFactory.sol";

/**
 * Deployer of ERC-20 Items proxies.
 */
contract ERC20ItemsFactory is IERC20ItemsFactory, SequenceProxyFactory {

    /**
     * Creates an ERC-20 Items Factory.
     * @param factoryOwner The owner of the ERC-20 Items Factory
     */
    constructor(
        address factoryOwner
    ) {
        ERC20Items impl = new ERC20Items();
        SequenceProxyFactory._initialize(address(impl), factoryOwner);
    }

    /// @inheritdoc IERC20ItemsFactoryFunctions
    function deploy(
        address proxyOwner,
        address tokenOwner,
        string memory name,
        string memory symbol,
        uint8 decimals,
        address implicitModeValidator,
        bytes32 implicitModeProjectId
    ) external returns (address proxyAddr) {
        bytes32 salt =
            keccak256(abi.encode(tokenOwner, name, symbol, decimals, implicitModeValidator, implicitModeProjectId));
        proxyAddr = _createProxy(salt, proxyOwner, "");
        ERC20Items(proxyAddr).initialize(
            tokenOwner, name, symbol, decimals, implicitModeValidator, implicitModeProjectId
        );
        emit ERC20ItemsDeployed(proxyAddr);
        return proxyAddr;
    }

    /// @inheritdoc IERC20ItemsFactoryFunctions
    function determineAddress(
        address proxyOwner,
        address tokenOwner,
        string memory name,
        string memory symbol,
        uint8 decimals,
        address implicitModeValidator,
        bytes32 implicitModeProjectId
    ) external view returns (address proxyAddr) {
        bytes32 salt =
            keccak256(abi.encode(tokenOwner, name, symbol, decimals, implicitModeValidator, implicitModeProjectId));
        return _computeProxyAddress(salt, proxyOwner, "");
    }

}
